package DSA_JAVA_DAY_1_2_Escape;

public class EscapeSequence {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("hello \"hello\"....");
		System.out.println("hello \\hello\\....");
		System.out.println("hello\tSir\tGoodMorning");
		System.out.println("hello,\nSir\tGoodMorning");
		System.out.println("hello\bSir\bGoodMorning");
		System.out.println("hello'Sir'GoodMorning");
		System.out.println("hello\"Sir\"GoodMorning");
		
		
		
	}

}
